def getchar
    begin
        system("stty raw -echo")
        str = STDIN.getc
      ensure
        system("stty -raw echo")
      end
      p str.chr
end

def wait_for_key_event
    puts"appuyez sur une touche pour continuer"
    getchar
end

def tab_elements(tableau)
    puts"le tableau contient #{tableau.count} éléments"
end

def tab_min_element(tableau)
   puts tableau.min_by{|x| x.length}#parcour le tableau et sors le plus petit
end

def howmany_handle(handle_length, tableau)
    puts"voici les éléments qui ont #{handle_length} caractères sans compter le @"
    puts "ils sont au nombre de #{tableau.count{|x| x.length==handle_length+1}}"
    puts "les voici"
    puts tableau.select{|x| x.length==handle_length+1}

end

def howmany_upper(tableau)
    puts tableau.count{|str| str[1]==str[1].upcase} - tableau.count{|str| str[1]=='_'}
end
def sort_tableau_alpha(tableau)
    return tableau.sort_by{|str| str.downcase}
end

def sort_tableau_size(tableau)
    return tableau.sort_by{|str| str.length}
end

def position(string,tableau)
    return tableau.index(string)
end

def size_information(tableau)
    for i in 1..(tableau.max_by{|x| x.length}).length
        if (tableau.count{|str| str.length==i})==0
            next
        end
        puts "il y a #{tableau.count{|str| str.length==i}} narvalo de taille #{i}"
    end
end

def presentation_menu_3viandes_sauce_biggy_algérienne_supplément_chédar
    puts"voici le menu chef"
    puts"1-Nombre élément tableau"
    puts"2-Plus petit élément tableau"
    puts"3-Combien il y a t'il d'element de taille N dans le tableau"
    puts"4-Combien il y a t'il d'element commençant par une masjuscule narvalo"
    puts"5-tableau trier par ordre alphabétique (attention au yeux sa pique très fort)"
    puts"6-tableau trier en fonction de la taille"
    puts"7-permet de trouver la position de l'élément voulue dans le tableau si existant"
    puts"8-affiches les informations général des éléments du tableau"
    puts">1 || < 8 et different de chiffre -quitter le programme"
    puts"veuillez choisir une valeur"
    return gets.to_i
end

def menu_3viandes_sauce_biggy_algérienne_supplément_chédar
    choix=1
    while choix >=1 && choix <=8
        while true
            choix=presentation_menu_3viandes_sauce_biggy_algérienne_supplément_chédar
            if choix==1
                tab_elements($tab)
                wait_for_key_event
                break
            elsif choix==2
                puts tab_min_element($tab)
                wait_for_key_event
                break
            elsif choix==3
                puts"veuillez choisir la taille à filtrer"
                puts howmany_handle(size=gets.to_i,$tab)
                wait_for_key_event
                break
            elsif choix==4
                puts howmany_upper($tab)
                wait_for_key_event
                break
            elsif choix==5
                puts sort_tableau_alpha($tab)
                wait_for_key_event
                break
            elsif choix==6
                puts sort_tableau_size($tab)
                wait_for_key_event
                break
            elsif choix==7
                puts"que voulez vous cherchez"
                puts position(ask=gets.chomp,$tab)
                wait_for_key_event
                break
            elsif choix==8
                puts size_information($tab) # une érreur très bizzare venant surement du langages ruby ce fait apparaitre a la fin de selection de cela 1..33 est affiché alors que la fonction lancé toute seule n'affiche pas ce 1..33 a vous de testé felix est sur le coup askip
                wait_for_key_event
                break
            else
                break
            end
        end
    end
    puts"merci de votre utilisation frero"
end

